# operasi aritmatika
a = 10
b = 3

# operasi penjumlahan
hasil = a + b 
print(a ,'+',b,'=', hasil)

# operasi pengurangan 
hasil = a - b
print(a, '-',b,'=', hasil)


# operasi perkalian
hasil = a * b 
print(a, '*', b, '=', hasil)

# operasi pembagian 
hasil = a / b 
print(a, '/', b, '=', hasil) 

# operasi eksponen/pangkat
hasil = a ** b
print(a, '**', b, '=', hasil)

# operasi modulus / hasil sisa pembagian
hasil = a % b
print(a, '%', b, '=', hasil)

# operasi floor division /dibulatkan kebawah
hasil = a // b
print(a, '//', b, '=', hasil)

# prioritas operasi

x = 3
y = 2
z = 4

hasil = x * x % y ** z
print(x, '*', x, '%', y ,'**', z, '=', hasil)

# hirarki atau tingkatan dalam aritmatika
# - ()
# -eksponen (**)
# -perkalian/pembagian (*, /, **, %, //)
# -pertambahan/pengurangan (+, -)