# operasi yang dapat dilakukan dengan penyingkatan
# oprasi ditambah dengan assigment

a = 5 #ini adalah assigment
print('nilai a adalah',a)

# a = a + 1
a += 1 #artinya adalah a = a + 1
print('nilai a += 1 menjadi',a)

a -= 2 #artinya adalah a = a - 2
print('nilai a -= 2 menjadi',a)

a *= 5 #artinya adalah a = a * 5
print('nilai a *= 5 menjadi',a)

a /= 2 #artinya adalah a = a / 2
print('nilai a /= 2 menjadi',a)

b = 10 
print('\nnilai b',b)

b %= 3
print('hasil modulus dari b %=3 adalah ',b)

b = 10 
print('\nnilai b',b)

b //= 3
print('hasil modulus dari b //=3 adalah ',b)

a = 5
a **= 3
print('hasil modulus dari b **=3 adalah ',a)


